import UserActinTypes from './user.types';

export const setCurrentUser = user => ({
    type: UserActinTypes.SET_CURRENT_USER,
    payload: user
});

export const googleSignInStart = () => ({
    type: UserActinTypes.GOOGLE_SIGN_IN_START
});

export const signInSuccess = (user) => ({
    type: UserActinTypes.GOOGLE_SIGN_IN_SUCCESS,
    payload: user
});

export const signInFailure = error => ({
    type: UserActinTypes.GOOGLE_SIGN_IN_FAILURE,
    payload: error
});

export const emailSignInStart = emailAndAPassword => ({
    type: UserActinTypes.EMAIL_SIGN_IN_START,
    payload: emailAndAPassword
});
