import UserActinTypes from './user.types';

export const googleSignInStart = () => ({
    type: UserActinTypes.GOOGLE_SIGN_IN_START
});

export const signInSuccess = (user) => ({
    type: UserActinTypes.SIGN_IN_SUCCESS,
    payload: user
});

export const signInFailure = error => ({
    type: UserActinTypes.SIGN_IN_FAILURE,
    payload: error
});

export const emailSignInStart = emailAndAPassword => ({
    type: UserActinTypes.EMAIL_SIGN_IN_START,
    payload: emailAndAPassword
});

export const checkUserSession = () => ({
    type: UserActinTypes.CHECK_USER_SESSION
});

export const signOutStart = () => ({
    type: UserActinTypes.SIGN_OUT_START
});

export const signOutSuccess = () => ({
    type: UserActinTypes.SIGN_OUT_SUCCESS
});

export const signOutFailure = (error) => ({
    type: UserActinTypes.SIGN_OUT_FAILURE,
    payload: error
})
